// Load defaults
var vrms = global.get("vrms");
var solar = global.get("solar");
var use = global.get("use");
var excess = global.get("excess");
var max_charge_current = global.get("max_charge_current");
var min_charge_current = global.get("min_charge_current");
var charge_current = max_charge_current;
var mode = global.get("mode");

// Get VRMS voltage
if (msg.topic === "vrms") {
    vrms = msg.payload;
    global.set("vrms",vrms);
}

// Get Solar PV gen & Convert to current (A)
if (msg.topic === "solar") {
    solar = msg.payload / vrms;
    global.set("solar",solar)
}

// Get on-site consumption & Convert to current (A)
if (msg.topic === "use") {
    use = msg.payload / vrms;
    global.set("use",use)
}


// Eco Mode
if (mode == 1){
    if (solar >= min_charge_current){
        charge_current = solar;
    }
    else {
        charge_current = min_charge_current;
    }
}

// Eco+ Mode
if (mode == 2){
    excess = solar - use;
    if (excess >= min_charge_current){
        charge_current = excess;
    }
    else {
        // Pause charging if no excess current
        charge_current = 0; 
    }
}

// Normal Charge
if (mode == 3){
    charge_current = max_charge_current;
}

charge_current=Math.round(charge_current);

return [{payload: charge_current}, {payload: mode}];

// Default to normal charge mode
global.set("mode",3);

// Set min & max charging current (A)
// If max charge current is greater than the max  
// allowed current set in OpenEVSE charge rate will be max allowed
global.set("max_charge_current",32);

// 6A min is defined by SAE/IEC standard
global.set("min_charge_current",6);

// Charge current can be varied in 1A increments as defined by SAE/IEC

// Default VRMS voltage
global.set("vrms",240);


return msg;
